import { Component, Inject } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { IUser } from '../models/interfaces';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-dialog-edit-info',
  templateUrl: './dialog-edit-info.component.html',
  styleUrls: ['./dialog-edit-info.component.less']
})
export class DialogEditInfoComponent {
  constructor(@Inject(MAT_DIALOG_DATA) public data : any){

  }
}
<h2 mat-dialog-title>Расскажите о себе</h2>
<mat-dialog-content>
    <form onsubmit="return false">
        <div class="form">
            <input placeholder="Имя" [(ngModel)]="this.userData.name" name="name" required>
            <input placeholder="Фамилия" [(ngModel)]="this.userData.lastName" name="lastName" required>
            <input placeholder="Дата рождения" [(ngModel)]="this.userData.birthday" name="birthday" required>
            <input placeholder="Родной город" [(ngModel)]="this.userData.nativeCity" name="nativeCity" required>
            <input placeholder="Любимая цитата" [(ngModel)]="this.userData.quote" name="quote" required>


        </div>
    </form>
</mat-dialog-content>
<mat-dialog-actions>
    <button mat-button mat-dialog-close mat-dialog-close="true">Отправить</button>
    <button mat-button mat-dialog-close mat-dialog-close="false">Отменить</button>
</mat-dialog-actions>
