import { Component, Inject } from '@angular/core';
import { FormsModule } from '@angular/forms';
import { IUser } from '../models/interfaces';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-dialog-edit-info',
  templateUrl: './dialog-edit-info.component.html',
  styleUrls: ['./dialog-edit-info.component.less']
})
export class DialogEditInfoComponent {
  user: IUser;

  constructor(@Inject(MAT_DIALOG_DATA) public data : any){

  }
}
<h2 mat-dialog-title>Расскажите о себе, {{ data.name }}</h2>
<mat-dialog-content>
    <form onsubmit="return false">
        <div class="form">
            <label for="name">Имя</label>
            <input placeholder="Имя" [(ngModel)]="this.user.name" value={{data.name}} name="name" required>

            <label for="lastName">Фамилия</label>
            <input placeholder="Фамилия" value={{data.lastName}} name="lastName" required>

            <label for="birthday">Дата рождения</label>
            <input placeholder="Дата рождения" value={{data.birthday}} name="birthday" required>

            <label for="nativeCity">Город</label>
            <input placeholder="Родной город" value={{data.nativeCity}} name="nativeCity" required>

            <label for="quote">Любимая цитата</label>
            <input placeholder="Любимая цитата" value={{data.quote}} name="quote" required>
        </div>
    </form>
</mat-dialog-content>
<mat-dialog-actions>
    <button mat-button mat-dialog-close mat-dialog-close="true">Отправить</button>
    <button mat-button mat-dialog-close mat-dialog-close="false">Отменить</button>
</mat-dialog-actions>
