import { Component, Input, OnInit } from '@angular/core';
import { IMsg } from 'src/app/models/interfaces';
import { UserGet } from 'src/app/services/user.service';
import { User } from '../user/user.component';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'chat',
  templateUrl: './chat.component.html',
  styleUrls: ['./chat.component.less']
})
export class ChatComponent{
  message : string
  msgs : IMsg[]
  msg : IMsg
  @Input() id_chat : number
  @Input() set Msgs(msgs: IMsg[]) {
    this.msgs = msgs;
  }

  constructor(private info : UserGet, private route : ActivatedRoute){

  }

  SendMsg(): void{
    this.msg = { text : this.message, author : "null", author_id : this.route.snapshot.params['id'] }
    this.message = ''
  }
}
<div class="chat">
    <div *ngIf="this.id_chat == -1" class="absent-content">
        <h1>Выберите чат</h1>
    </div>

    <div *ngIf="this.id_chat != -1" class="messages">
        <ul>
            <li *ngFor="let msg of msgs; let idx = index" [ngClass]="{'comp' : this.id_chat == msg.author_id, 'mine' : this.id_chat != msg.author_id}" id="idx">

                    <h3>{{msg.author}}</h3>

                    <pre>{{msg.text}}</pre>


            </li>
        </ul>
    </div>

    <div *ngIf="this.id_chat != -1" class="input-field">
        <textarea placeholder="Введите сообщение..." [(ngModel)]="this.message" (keyup.enter)="SendMsg()"></textarea>
        <button (click)="SendMsg()"></button>
    </div>
</div>
