import { Component, OnInit } from "@angular/core";
import { Header } from "../header/header.component";
import { News } from "../news/news.component";
import { User } from "../user/user.component";
import { IUser, INews } from "src/app/models/interfaces";
import { UserGet } from "src/app/services/user.service";

@Component({
    selector: 'mainPage',
    templateUrl: './mainPage.component.html',
    styleUrls: ['./mainPage.component.less']
})

export class mainPage implements OnInit{
    title = "АлексБибл"
    user : IUser
    news : INews[] = []

    constructor(private userData : UserGet){}

    ngOnInit(): void {
      this.userData.GetUserData("0").subscribe(user => {
        this.user = user
      })
      this.userData.GetUserNews("0").subscribe(news => {
        this.news = news
      })
    }
}
