import { Injectable } from "@angular/core";
import {HttpClient} from '@angular/common/http'
import { Observable } from "rxjs";
import { INews, IUser } from "../models/interfaces";

@Injectable({
    providedIn: 'root'
})

export class UserGet{
    constructor(private http: HttpClient){}

    GetUserData(num : string): Observable<IUser>{
        return this.http.get<IUser>('http://localhost:3000/userInfo/' + num)
    }

    GetUserNews(num : string): Observable<INews[]>{
        return this.http.post<INews[]>('http://localhost:3000/postsList', {content: [num], with : true})
    }
}
