import { Component, OnInit } from '@angular/core';
import { UserGet  } from './services/user.service';
import { INews, IUser } from './models/interfaces';


@Component({
  selector: 'page',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.less']
})

export class AppComponent implements OnInit{
  title = "АлексБибл"
  user : IUser
  news : INews[] = []

  constructor(private userData : UserGet){}

  ngOnInit(): void {
    this.userData.GetUserData("0").subscribe(user => {
      this.user = user
    })
    this.userData.GetUserNews("0").subscribe(news => {
      this.news = news
    })
  }
}
