import { Component, Inject } from '@angular/core';
import { FormsModule, NgForm } from '@angular/forms';
import { IUser } from '../models/interfaces';
import { MAT_DIALOG_DATA } from '@angular/material/dialog';

@Component({
  selector: 'app-dialog-edit-info',
  templateUrl: './dialog-edit-info.component.html',
  styleUrls: ['./dialog-edit-info.component.less']
})
export class DialogEditInfoComponent {
  user: IUser;

  constructor(@Inject(MAT_DIALOG_DATA) public data : any){
    console.log(data)
  }

  SendForm(data : NgForm): void{

  }
}
<h2 mat-dialog-title>Расскажите о себе, {{ data.name }}</h2>
<mat-dialog-content>
    <form onsubmit="SendForm(UserInfo)" #UserInfo="ngForm">
        <div class="form">
            <label for="name">Имя</label>
            <input placeholder="Имя" value={{this.data.name}} name="name" required>

            <label for="lastName">Фамилия</label>
            <input placeholder="Фамилия" value={{this.data.lastName}} name="lastName" required>

            <label for="birthday">Дата рождения</label>
            <input placeholder="Дата рождения" value={{this.data.birthday}} name="birthday" required>

            <label for="nativeCity">Город</label>
            <input placeholder="Родной город" value={{this.data.nativeCity}} name="nativeCity" required>

            <label for="quote">Любимая цитата</label>
            <input placeholder="Любимая цитата" value={{this.data.quote}} name="quote" required>
        </div>
    </form>
</mat-dialog-content>
<mat-dialog-actions>
    <button mat-button mat-dialog-close mat-dialog-close='this.user'>Отправить</button>
    <button mat-button mat-dialog-close mat-dialog-close="null">Отменить</button>
</mat-dialog-actions>
