import { Component, OnDestroy, OnInit } from "@angular/core";
import { IUser } from "src/app/models/interfaces";
import { UserGet } from "src/app/services/user.service";
import { FormsModule } from "@angular/forms";

@Component({
    selector: 'formPage',
    templateUrl: './formReg.component.html',
    styleUrls: ['./formReg.component.less']
})

class User implements IUser{
    constructor(){
        id: -1
        role: "user"
        name: ""
        lastName: ""
        quote: string,
        birthday: string,
        nativeCity: string,
        email: string, 
        status: string,
        freinds: any,
        password: string
    }
}

export class FormPage implements OnInit{
    enter: boolean = true

    users: IUser[] = []
    status: string = ""
    user: IUser

    constructor(private UserData : UserGet){}


    ngOnInit(): void {
        this.UserData.GetUsers().subscribe(users =>{
            this.users = users
        }) 
    }

    test(){
        console.log(this.user.email)
    }

    Search(email: string, code: string): number{
        for(let i = 0; i < this.users.length; i++){
            if(this.users[i].email == email){
                if(this.users[i].password == code){
                    return 0;
                }
                else{
                    return 2 //неверный пароль
                }
            }
        }
        return 1 //пользователь не найден
    }

    Enter(email: string, code: string){
        let num = this.Search(email, code)

        switch(num){
            case 0:
                this.status = "Всё окей!"
                break
            case 1:
                this.status = "Пользователь не найден!"
                break
            case 2:
                this.status = "Неверный код доступа!"
                break            
        }
    }

    Reg(){

    }
}
